import React from "react";
import "./Progress.scss";
import { illustration, techStack } from "../../portfolio";
import { Fade } from "react-reveal";
import Build from "../../assets/lottie/build";
import DisplayLottie from "../../components/displayLottie/DisplayLottie";
import skillImage from "../../assets/images/skill.svg";
import anotherImage from "../../assets/images/another_image.svg";

export default function StackProgress() {
  if (techStack.viewSkillBars) {
    return (
      <Fade bottom duration={1000} distance="20px">
        <div className="skills-container">
          <div className="skills-bar">
            <h1 className="skills-heading">Technical skills</h1>

            {/* Your skill bars code here */}

          </div>

          <div className="skills-image">
            {illustration.animated ? (
              <DisplayLottie animationData={Build} />
            ) : (
              <>
                {/* Include the first image */}
                <img
                  alt="Skills"
                  src={skillImage}
                />

                {/* Include the second image */}
                <img
                  alt="Another Image"
                  src={anotherImage}
                />
              </>
            )}
          </div>
        </div>
      </Fade>
    );
  }
  return null;
}
